def mult(s):
    ret = 1
    for i in s:
        ret *= int(i)

    return ret

s = ""
s = s.split('0')
ret = 0
for subseq in s:
    if (len(subseq)<13):
        tmp = mult(subseq)
        if tmp>ret:
             ret = tmp
    else:
        for i in range(len(subseq)-12):
            tmp = mult(subseq[i:i+13])
            if tmp>ret:
                ret = tmp

print(ret)


            
